import React, { useState } from "react";
import classes from "./signup.module.css";
import Icons from "./sign-up-icons";
import Input from "./custominput";
function Signup(props) {
  const [namestyle, setnamestyle] = useState("");
  const [name, setname] = useState("");
  const {
    EnteredValue: EnteredFirstName,
    ValueIsValid: FullNameIsValid,
    valueInputChangeHandler: FirstNameInputChangeHandler,
    setfocus,
    HasError,
    blurHandler,
    reset: FirstNameReset,
  } = Input((EnteredFirstName) => EnteredFirstName.trim().length > 0);
  const {
    EnteredValue: EnteredEmail,
    ValueIsValid: EnteredEmailIsValid,
    valueInputChangeHandler: emailHandler,
    setfocus: setEmailFocus,
    HasError: emailerror,
    blurHandler: emailblur,
    reset: EmailReset,
  } = Input((EnteredEmail) => EnteredEmail.includes("@"));
  const submitHandler = (e) => {
    e.preventDefault();
  };
  return (
    <div className={`${classes.formContainer} `}>
      <form onSubmit={submitHandler}>
        <h1 className={classes.header}>Create Account</h1>
        <Icons />
        <span>or use your email for registration</span>
        <input
          value={EnteredFirstName}
          className={HasError && classes.invalid}
          style={{ namestyle }}
          type="text"
          placeholder="Full Name"
          onChange={FirstNameInputChangeHandler}
          onBlur={blurHandler}
        />
        <input type="email" placeholder="E-mail" />
        <input type="password" placeholder="Password" />
        <input type="date" />
        <select defaultValue="gender" name="gender">
          <option value="gender" disabled hidden>
            GENDER
          </option>
          <option value="MALE">MALE</option>
          <option value="FEMALE">FEMALE</option>
        </select>
        <button>Sign Up</button>
      </form>
    </div>
  );
}

export default Signup;
