import React from "react";
import { useState } from "react";
import "./intro.css";
function Intro(props) {
  const [signup, setsignup] = useState(false);
  const buttonHandler = (e) => {
    e.preventDefault();
    setsignup((prev) => !prev);
    console.log(signup);
  };
  const intro =
    "<h1>HELLO,FRIEND!</h1> <p>enter your personal details and start journey with us </p>";
  const state = signup ? "SIGN IN" : "SIGN UP";
  return (
    <div className="overlay">
      <div className="intro">
        {intro}
        <button onClick={buttonHandler}> {state}</button>
      </div>
    </div>
  );
}

export default Intro;
