import Signup from "./components/Signup";
import Layout from "./components/layout/layout";
import Intro from "./components/intro";
import { useEffect, useState } from "react";
import Login from "./components/signIn";
function App() {
  const [data, setdata] = useState("");
  const [form, setform] = useState(false);
  const userinput = (data) => {
    setdata(data);
  };

  const question = {
    "How many legs does a spider have?": { 1: 5, 2: 6, 3: 7, 4: 8 },
    "What is the name of the toy cowboy in Toy Story?": {
      1: "woody",
      2: "candy",
      3: "nikolas",
      4: "been",
    },
    "What is the color of an emerald?": {
      1: "blue",
      2: "green",
      3: "yellow",
      4: "orange",
    },
    "What is something you hit with a hammer?": {
      1: "book",
      2: "pen",
      3: "paper",
      4: "nail",
    },
    "What colors are the stars on the American flag?": {
      1: "white",
      2: "green",
      3: "yellow",
      4: "orange",
    },
    "How many planets are in our solar system?": {
      1: 5,
      2: 6,
      3: 7,
      4: 8,
    },
  };
  useEffect(() => {
    const HttpRequest = async () => {
      await fetch(
        "https://login-form-b66cc-default-rtdb.firebaseio.com/signup.json",
        {
          method: "PUT",
          body: JSON.stringify(data),
        }
      );
    };
    HttpRequest();
  }, [data]);
  return (
    <Layout>
      <Intro setform={setform} />
      {form ? <Signup data={userinput} /> : <Login />}
    </Layout>
  );
}

export default App;
